	private $container;

	public function __construct(Customweb_Payment_Authorization_IPaymentMethod $paymentMethod, Customweb_DependencyInjection_IContainer $container){
		parent::__construct($paymentMethod);
		if (!$container instanceof Customweb_Stripe_Container) {
			$container = new Customweb_Stripe_Container($container);
		}
		$this->container = $container;
	}

	public function getCaptureMode(){
		if (!$this->existsPaymentMethodConfigurationValue('capturing')) {
			return null;
		}
		if ($this->getPaymentMethodConfigurationValue('capturing') == 'direct') {
			return 'true';
		}
		return 'false';
	}

	public function isSourcePendingAuthorized(){
		$parameters = $this->getPaymentMethodParameters();
		return (isset($parameters['source_pending_authorized']) && $parameters['source_pending_authorized'] == 'true');
	}

	public function getVisibleFormFields(Customweb_Payment_Authorization_IOrderContext $orderContext, $aliasTransaction, $failedTransaction, $paymentCustomerContext){
		return array();
	}

	/**
	 * This function is called in the source response processor, and can be used to set any additional payment-method specific information on the
	 * transaction.
	 *
	 * @param Customweb_Stripe_Authorization_Transaction $transaction
	 * @param array $json
	 */
	public function processPaymentInformation(Customweb_Stripe_Authorization_Transaction $transaction, array $json){}

	/**
	 * This function is called in the source response processor, and is expected to return the display string used for the alias.
	 *
	 * @param array $json
	 * @return string
	 * @throws Exception
	 */
	public function extractAliasForDisplay(array $json){
		throw new Exception("Payment method does not support alias.");
	}

	protected function getPaymentInformationMap(){
		return self::$paymentMapping;
	}

	protected function getContainer(){
		return $this->container;
	}
