INSERT INTO `oc_modification` (`modification_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES
(1, 'd_admin_menu', 'd_admin_menu', 'Dreamvention', '1.0.0', 'http://www.dreamvention.ee/', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n    <name>d_admin_menu</name>\n    <code>d_admin_menu</code>\n    <description>Admin Menu</description>\n    <version>1.0.0</version>\n    <author>Dreamvention</author>\n    <link>http://www.dreamvention.ee/</link>\n\n        <!-- 201-220 -->\n        <file path=\"admin/view/template/common/column_left.tpl\">\n            <operation error=\"skip\">\n                <search><![CDATA[<nav id=\"column-left\"><?php echo $profile; ?><?php echo $menu; ?><?php echo $stats; ?></nav>]]></search>\n                <add position=\"replace\"><![CDATA[\n                    <nav id=\"column-left\"><?php echo $profile; ?><?php echo $menu; ?><?php echo (isset($d_admin_menu)) ? $d_admin_menu : \'\'; ?><?php echo $stats; ?></nav>\n                ]]></add>\n            </operation>\n        </file>\n\n        <!-- 230 -->\n        <file path=\"admin/view/template/common/column_left.tpl\">\n            <operation error=\"skip\">\n                <search><![CDATA[<div id=\"stats\">]]></search>\n                <add position=\"before\"><![CDATA[\n                    <?php echo (isset($d_admin_menu)) ? $d_admin_menu : \'\'; ?>\n                ]]></add>\n            </operation>\n        </file>\n\n        <!-- OC 3.x compatibility START -->\n        <file path=\"admin/view/template/common/column_left.twig\">\n            <operation error=\"skip\">\n                <search><![CDATA[<div id=\"stats\">]]></search>\n                <add position=\"before\"><![CDATA[\n                    {{ d_admin_menu }}\n                ]]></add>\n            </operation>\n        </file>\n        <!-- OC 3.x compatibility END -->\n\n</modification>\n', 1, '2019-10-03 10:36:55'),
(2, 'd_twig_manager', 'd_twig_manager', 'Dreamvention', '1.0.0', 'http://dreamvention.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n    <name>d_twig_manager</name>\n    <code>d_twig_manager</code>\n    <description>Adds support for twig templating engine</description>\n    <version>1.0.0</version>\n    <author>Dreamvention</author>\n    <link>http://dreamvention.com</link>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"add event support for 2.1.0.0 and below\">\n            <search><![CDATA[$file = DIR_TEMPLATE . $template;]]></search>\n            <add position=\"before\"><![CDATA[\n            //d_twig_manager.xml 1\n            $output = $this->controller(\'event/d_twig_manager/support\', array(\'route\' => $template, \'data\' => $data));\n            if($output || !file_exists( DIR_TEMPLATE . $template)){\n                return $output;\n            }\n            ]]></add>\n        </operation>\n        <operation error=\"skip\" info=\"add event support for 2.2.0.0 and above\">\n            <search><![CDATA[$output = $template->render($route . \'.tpl\']]></search>\n            <add position=\"before\"><![CDATA[\n            //d_twig.xml 2\n\n            $output = $this->controller(\'event/d_twig_manager/support\', array(\'route\' => $route, \'data\' => $data));\n            if(!$output && file_exists( DIR_TEMPLATE . $route . \'.tpl\'))\n            ]]></add>\n        </operation>\n    </file>\n</modification>', 1, '2019-10-03 10:38:02'),
(3, 'd_event_manager', 'd_event_manager', 'Dreamvention', '3.0.0', 'http://dreamvention.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n    <name>d_event_manager</name>\n    <code>d_event_manager</code>\n    <description>Add support for events to versions before 2.3.0.0 </description>\n    <version>3.0.0</version>\n    <author>Dreamvention</author>\n    <link>http://dreamvention.com</link>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"add event support 2.1.0.0 and below\">\n            <search><![CDATA[public function __construct($registry) {]]></search>\n            <add position=\"after\"><![CDATA[\n    //d_event_manager.xml loader\n    $this->load = new d_event_manager\\Loader($this, $registry);\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"add event support 2.1.0.0 and below\">\n            <search><![CDATA[public function controller($route, $args = array()) {]]></search>\n            <add position=\"replace\"><![CDATA[\n    //d_event_manager.xml controller\n    public function controller($route, $args = array()) {\n        return $this->load->controller($route, $args);\n    }\n    \n    //this is the original controller method which is called by the d_event_menager\\Loader -> contorller method\n    public function _controller($route, $args = array()) {\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"add event support 2.1.0.0 - controller has $data instead or $args\">\n            <search><![CDATA[public function controller($route, $data = array()) {]]></search>\n            <add position=\"replace\"><![CDATA[\n    //d_event_manager.xml controller\n    public function controller($route, $args = array()) {\n        return $this->load->controller($route, $args);\n    }\n    \n    //this is the original control