INSERT INTO `morp_cms_content` VALUES (10, 3, 0, 0, '', 't2__headline_H2@@DUSCH- UND KÜCHENRÜCKWÄNDE --##t2__fliesstext@@Unsere Wandverkleidungen erhaltet Ihr exakt nach Euren Maßvorgaben gefertigt.\r\n\r\nPlateART Rückwände aus Alu-Verbund oder Kunststoff sind der innovative Trend für modernes Bad- Küchen- und Wohn-Design. Ohne Fugen und leicht auf jeder tragfähigen Wand zu montieren, setzen sie neue Hygienestandards in allen Naßbereichen.\r\n\r\nPlateART bietet detailiert aufgelöste, moderne Oberflächen-Dekore in brillanter Farbwiedergabe. Die neue PlateART premium ergänzt durch eine glänzende Oberfläche mit einer Zusatzversiegelung unser Abgebot. Diese zeigt eine erhöhte Kratzfestigkeit und Beständigkeit gegen Lösemittel und Chemikalien.##t2__bild@@3|##', 1, 1, 0, 0, 0, 0, 1, 0, 0, 0, 2, 2, 2, '', '', '', '', 0, 0, '', 1, 0, 0);
INSERT INTO `morp_cms_content` VALUES (11, 3, 0, 0, '', 't2__bild@@1|##t2__headline_H2@@LEICHT MONTIERT##t2__fliesstext@@Im Bad- und Küchenbereich erfolgt die Befestigung der Rückwände bequem mit Montagekleber oder Silikon. Ein Abschlagen der alten Fliesen ist bei ausreichender Tragfähigkeit nicht notwendig und reduziert so den Arbeitsaufwand und den Schmutz erheblich. Unsere innovativen Wandverkleidungen aus STADUR-Leichtschaum überzeugen durch einfachste Handhabung und können sogar mit Spiegelklebeband auf jeder tragfähigen Wand befestigt werden - Tapezieren war gestern!##', 1, 1, 0, 0, 0, 0, 1, 0, 0, 0, 2, 2, 3, '', '', '', '', 0, 0, '', 1, 1, 0);
