INSERT INTO `morp_cms_content_history` VALUES (48, '', '2019-10-08 23:00:19', 1, 1, 0, 0, '', 't2__farbe@@5##t2__bild@@3|##t2__headline_H2@@DUSCH- UND KÜCHENRÜCKWÄNDE --##t2__fliesstext@@Unsere Wandverkleidungen erhaltet Ihr exakt nach Euren Maßvorgaben gefertigt.\r\n\r\nPlateART Rückwände aus Alu-Verbund oder Kunststoff sind der innovative Trend für modernes Bad- Küchen- und Wohn-Design. Ohne Fugen und leicht auf jeder tragfähigen Wand zu montieren, setzen sie neue Hygienestandards in allen Naßbereichen.\r\n\r\nPlateART bietet detailiert aufgelöste, moderne Oberflächen-Dekore in brillanter Farbwiedergabe. Die neue PlateART premium ergänzt durch eine glänzende Oberfläche mit einer Zusatzversiegelung unser Abgebot. Diese zeigt eine erhöhte Kratzfestigkeit und Beständigkeit gegen Lösemittel und Chemikalien.##', 1, 1, NULL, NULL, NULL, NULL, 1, 0, 0, 0, 0, 1, 0, NULL, NULL, '', '', 0, 0, '', 0);
INSERT INTO `morp_cms_content_history` VALUES (49, '', '2019-10-08 23:06:24', 3, 1, 0, 0, '', 't2__bild@@2|##t2__headline_H2@@UM DIE ECKE GEDACHT##t2__fliesstext@@Einzelplatten, Eck- oder Nischenlösungen, spannende Ideen für Eure Trend-Wände?\r\n\r\nMit unserem neuen und intuitiv bedienbaren Konfigurator könnt Ihr Eure Rückwände maßgenau anlegen und die gewünschten Motive ausprobieren. Wir liefern Euch die original PlateART Wandverkleidung frei Haus.\r\n\r\nUnsere Montageanleitung finden sie hier hier.##', 1, 1, NULL, NULL, NULL, NULL, 1, 0, 0, 0, 0, 1, 0, NULL, NULL, '', '', 0, 0, '', 0);
INSERT INTO `morp_cms_content_history` VALUES (50, '', '2019-10-08 23:07:20', 1, 1, 0, 0, '', 't2__farbe@@5##t2__headline_H2@@DUSCH- UND KÜCHENRÜCKWÄNDE --##t2__fliesstext@@Unsere Wandverkleidungen erhaltet Ihr exakt nach Euren Maßvorgaben gefertigt.\r\n\r\nPlateART Rückwände aus Alu-Verbund oder Kunststoff sind der innovative Trend für modernes Bad- Küchen- und Wohn-Design. Ohne Fugen und leicht auf jeder tragfähigen Wand zu montieren, setzen sie neue Hygienestandards in allen Naßbereichen.\r\n\r\nPlateART bietet detailiert aufgelöste, moderne Oberflächen-Dekore in brillanter Farbwiedergabe. Die neue PlateART premium ergänzt durch eine glänzende Oberfläche mit einer Zusatzversiegelung unser Abgebot. Diese zeigt eine erhöhte Kratzfestigkeit und Beständigkeit gegen Lösemittel und Chemikalien.##t2__bild@@3|##', 1, 1, NULL, NULL, NULL, NULL, 1, 0, 0, 0, 0, 1, 0, NULL, NULL, '', '', 0, 0, '', 0);
