INSERT INTO `morp_cms_content_history` VALUES (52, '', '2019-10-08 23:09:00', 11, 3, 0, 0, '', 't2__bild@@1|##t2__headline_H2@@LEICHT MONTIERT##t2__fliesstext@@Im Bad- und Küchenbereich erfolgt die Befestigung der Rückwände bequem mit Montagekleber oder Silikon. Ein Abschlagen der alten Fliesen ist bei ausreichender Tragfähigkeit nicht notwendig und reduziert so den Arbeitsaufwand und den Schmutz erheblich. Unsere innovativen Wandverkleidungen aus STADUR-Leichtschaum überzeugen durch einfachste Handhabung und können sogar mit Spiegelklebeband auf jeder tragfähigen Wand befestigt werden - Tapezieren war gestern!##', 1, 1, NULL, NULL, NULL, NULL, 1, 0, 0, 0, 0, 1, 0, NULL, NULL, '', '', 0, 0, '', 0);
