INSERT INTO `morp_cms_content_history` VALUES (77, '', '2019-10-15 23:36:31', 16, 4, 0, 0, '', 't4__headline@@##t4__fliesstext@@Wenn Du die Platte/n innerhalb der Duschkabine einbaust, lass zu den Schienen der Duschwand ca. 3 mm Luft. So hast Du eine definierte Fuge, die Du dann gut mit Silikon füllen kannst.\r\n\r\nBei einer Ecklösung solltest Du zusätzlich 3 mm Luft in der Ecke lassen, um die Platten spitz stossend montieren zu können.\r\n\r\nAls Fausregel gilt: Die Platte sollte ca. 5-6 mm schmaler sein, als das gemessene, lichte Wandmaß. Wenn Du die Platte auf die Duschtasse/ den Boden aufsetzt, solltest Du auch hier einen kleinen Abstand einkalkulieren.##', 1, 1, NULL, NULL, NULL, NULL, 1, 0, 0, 0, 0, 1, 0, NULL, NULL, '', '', 0, 0, '', 0);
INSERT INTO `morp_cms_content_history` VALUES (78, '', '2019-10-15 23:36:44', 16, 4, 0, 0, '', 't4__headline@@Wie ermittle ich die richtige Größe für meine plateART Rückwand?##t4__fliesstext@@Wenn Du die Platte/n innerhalb der Duschkabine einbaust, lass zu den Schienen der Duschwand ca. 3 mm Luft. So hast Du eine definierte Fuge, die Du dann gut mit Silikon füllen kannst.\r\n\r\nBei einer Ecklösung solltest Du zusätzlich 3 mm Luft in der Ecke lassen, um die Platten spitz stossend montieren zu können.\r\n\r\nAls Fausregel gilt: Die Platte sollte ca. 5-6 mm schmaler sein, als das gemessene, lichte Wandmaß. Wenn Du die Platte auf die Duschtasse/ den Boden aufsetzt, solltest Du auch hier einen kleinen Abstand einkalkulieren.##', 1, 1, NULL, NULL, NULL, NULL, 1, 0, 0, 0, 0, 1, 0, NULL, NULL, '', '', 0, 0, '', 0);
INSERT INTO `morp_cms_content_history` VALUES (79, '', '2019-10-15 23:40:03', 18, 4, 0, 0, '', 't4__headline@@Wie ermittle ich die richtige Größe für meine plateART Rückwand?##t4__fliesstext@@Wenn Du die Platte/n innerhalb der Duschkabine einbaust, lass zu den Schienen der Duschwand ca. 3 mm Luft. So hast Du eine definierte Fuge, die Du dann gut mit Silikon füllen kannst.\r\n\r\nBei einer Ecklösung solltest Du zusätzlich 3 mm Luft in der Ecke lassen, um die Platten spitz stossend montieren zu können.\r\n\r\nAls Fausregel gilt: Die Platte sollte ca. 5-6 mm schmaler sein, als das gemessene, lichte Wandmaß. Wenn Du die Platte auf die Duschtasse/ den Boden aufsetzt, solltest Du auch hier einen kleinen Abstand einkalkulieren.##', 1, 1, NULL, NULL, NULL, NULL, 1, 0, 0, 0, 0, 1, 0, NULL, NULL, '', '', 0, 0, '', 0);
INSERT INTO `morp_cms_content_history` VALUES (80, '', '2019-10-15 23:40:46', 18, 4, 0, 0, '', 't4__headline@@Aus welchem Material besteht die plateART Platte?##t4__fliesstext@@Die plateART-Platte erhalten ihr in 4 verschiedenen Materialien:\r\n\r\n3 mm Aluminium-Verbund (matt oder glänzend)\r\n\r\n3 mm Aluminium-Verbund - gebürstet. Ähnlich einer gebürsteten Edelstahl-Oberfläche. Dieses Material muss für unsere Metall-Oberflächen (Gold, Kupfer...) gewählt werden\r\n\r\n3 mm Hartschaum\r\n\r\n5 mm Stadur - Leichtschaum##', 1, 1, NULL, NULL, NULL, NULL, 1, 0, 0, 0, 0, 1, 0, NULL, NULL, '', '', 0, 0, '', 0);
