INSERT INTO `morp_cms_content_history` VALUES (395, '', '2019-12-06 17:23:45', 22, 3, 0, 0, '', 't2__headline_H2@@KONFIGURATOR##t2__fliesstext@@Mit unserem intelligenten Konfigurator habt Ihr nach ein \r\npaar Klicks Euer individuelles Wand - Dekor.\r\n\r\nEinfach das gewünschte Motiv auswählen und \r\nschnittgenau auf den Millimeter bestellen. Die Lieferung\r\nist innerhalb Deutschlands und nach Östereich kostenfrei.\r\n\r\nBei Fragen hilft Euch unser motivierter Kundenservice\r\nschnell weiter. Profitiert von unserem Service des\r\nkostenlosen Expertenausschnitts vorab !!!\r\n\r\nKlicke auf das Bild um zum Konfigurator zu gelangen.##t2__bild@@35|2##', 1, 1, NULL, NULL, NULL, NULL, 1, 0, 0, 0, 0, 1, 0, NULL, NULL, '', '', 0, 0, '', 0);
INSERT INTO `morp_cms_content_history` VALUES (396, '', '2019-12-06 17:26:29', 21, 3, 0, 0, '', 't2__bild@@34|##t2__headline_H2@@SO KÖNNTE ES AUSSEHEN##t2__fliesstext@@Macht Euer Zuhause zu Eurem Lieblingsort !\r\nGanz einfach mit einer yourplate Duschrückwand.\r\n\r\nSonnenaufgang in der Dusche ?\r\nHochwertige Steinoptik hinter dem Herd ?\r\nEin Kunstwerk statt langweiliger Fliesen ?\r\nDer Fantasie sind keine Grenzen gesetzt !\r\n\r\nIn unserem Shop findet Ihr Dusch- und Küchen-\r\nrückwände zu festen Größen und von uns gewählten\r\nAusschnitten. Lasst Euch gerne inspirieren.\r\n\r\nKlickt Euch durch unsere liebevoll ausgesuchten\r\nMotive und passt sie im Konfigurator an.##', 1, 1, NULL, NULL, NULL, NULL, 1, 0, 0, 0, 0, 1, 0, NULL, NULL, '', '', 0, 0, '', 0);
