INSERT INTO `oc_letscms_faq_description` VALUES (16, 2, 'Alu Basic matt 3 mm', '&lt;div class=&quot;modal-header&quot; style=&quot;display: flex; align-items: flex-start; justify-content: space-between; padding: 1rem; border-bottom-color: rgb(233, 236, 239); border-top-left-radius: 0.3rem; border-top-right-radius: 0.3rem; color: rgb(14, 51, 69); font-family: Ubuntu; font-size: 16px;&quot;&gt;&lt;h5 style=&quot;margin-bottom: 0.5rem; line-height: 1.2; font-size: 1.25rem;&quot;&gt;Alu Basic matt 3 mm&lt;/h5&gt;&lt;/div&gt;&lt;div class=&quot;modal-body&quot; style=&quot;flex: 1 1 auto; padding: 1rem; color: rgb(14, 51, 69); font-family: Ubuntu; font-size: 16px;&quot;&gt;&lt;p style=&quot;margin-bottom: 1rem;&quot;&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom: 1rem;&quot;&gt;Unser Allrounder für Dusche, Küche und Wandverkleidung. Oberfläche &quot;plateART Basic Matt&quot;&lt;/p&gt;&lt;ul style=&quot;margin-bottom: 1rem;&quot;&gt;&lt;li&gt;3 mm starkes Aluminum- Verbund&lt;/li&gt;&lt;li&gt;hitzebeständig bis 100°C&lt;/li&gt;&lt;li&gt;leicht mit Stich,- Handkreissäge und Kronen-Bohrer zu bearbeiten&lt;/li&gt;&lt;li&gt;auf&amp;nbsp;jeder tragfähigen Wand mit Montagekleber oder Silikon zu befestigen&lt;/li&gt;&lt;li&gt;gut zu reinigen&amp;nbsp;(keine Scheuermittel verwenden!)&lt;/li&gt;&lt;li&gt;hochwertiger Druck mit seidenmatter, glatter Oberfläche&lt;/li&gt;&lt;li&gt;für den Innen- und Außenbereich geeignet&lt;/li&gt;&lt;/ul&gt;&lt;/div&gt;', NULL);
