INSERT INTO `oc_material` VALUES (2, 2, 'Alu Basic matt 3 mm', '&lt;p&gt;&lt;b&gt;Alu Basic matt 3 mm - Der Alleskönner!&lt;/b&gt;&lt;/p&gt;&lt;p&gt;&lt;b&gt;&lt;br&gt;- Hochwertiger digitaler Druck mit matter Oberfläche&lt;br&gt;- Alu-Verbund in 3mm Stärke&lt;br&gt;- Hitzebeständig bis 100°C&lt;br&gt;- Leicht zu bearbeiten!&amp;nbsp; (Handelsübliche Bohrer, Kronenbohrer oder Stichsäge)&lt;br&gt;- Für alle Nass- und Trockenbereiche verwendbar (Dusche, Küche, Bad und Wohnraumgestaltung)&lt;/b&gt;&lt;/p&gt;', 1, 1);
INSERT INTO `oc_material` VALUES (8, 2, 'Alu Robusto Schlag &amp; Kratzfest 3mm', '&lt;p&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;&lt;b&gt;Alu Robusto Schlag &amp;amp; Kratzfest 3 mm&lt;/b&gt;&lt;/span&gt;&lt;b&gt;&amp;nbsp;- Unser Flagschiff für die Ewigkeit&lt;/b&gt;&lt;/p&gt;&lt;p&gt;&lt;b&gt;&lt;br&gt;- Hochwertiger digitaler Druck mit einer seidig glänzenden Steinschlag-Beschichtung&lt;br&gt;- absolut Kratz &amp;amp; Schlagfest&amp;nbsp;&lt;br&gt;- Alu-Verbund in 3mm Stärke&lt;br&gt;- Hitzebeständig bis 100°C&lt;br&gt;- Leicht zu bearbeiten!&amp;nbsp; (Handelsübliche Bohrer, Kronenbohrer oder Stichsäge)&lt;br&gt;- Für alle Nass- und Trockenbereiche verwendbar (Dusche, Küche, Bad und Wohnraumgestaltung)&lt;/b&gt;&lt;/p&gt;', 0, 5);
INSERT INTO `oc_material` VALUES (4, 2, 'Alu premium gebürstet 3 mm', '&lt;p&gt;&lt;b&gt;Alu premium gebürstet 3 mm - Geschliffen gegriffen!&lt;/b&gt;&lt;/p&gt;&lt;p&gt;&lt;b&gt;&lt;br&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;- Hochwertiger digitaler Druck für eine gebürstete Metalloberfläche&lt;br&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;- Schreibt uns gerne Eure Wunschfarbe (Rose\' Gold, Gelbgold Kupfer oder andere Farben möglich)&lt;br&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;- Alu-Verbund in 3mm Stärke&lt;br&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;- Hitzebeständig bis 100°C&lt;br&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;- Leicht zu bearbeiten!&amp;nbsp; (Handelsübliche Bohrer, Kronenbohrer oder Stichsäge)&lt;br&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;- Für alle Nass- und Trockenbereiche verwendbar (Dusche, Küche, Bad und Wohnraumgestaltung)&lt;/span&gt;&lt;/b&gt;&lt;/p&gt;', 1, 4);
INSERT INTO `oc_material` VALUES (7, 2, 'Alu Premium matt 3 mm', '&lt;p&gt;&lt;b&gt;Alu Premium matt 3mm - Matt, aber Oh ho!&lt;/b&gt;&lt;/p&gt;&lt;p&gt;&lt;b&gt;&lt;br&gt;- Hochwertiger digitaler Druck mit einer edlen, matten Nano-Oberfläche (Schmutz perlt ab)&lt;br&gt;- Sehr komfortabel zu reinigen&lt;br&gt;- Höhere Kratzfestigkeit&lt;br&gt;- Alu-Verbund in 3mm Stärke&lt;br&gt;- Hitzebeständig bis 100°C&lt;br&gt;- Leicht zu bearbeiten!&amp;nbsp; (Handelsübliche Bohrer, Kronenbohrer oder Stichsäge)&lt;br&gt;- Für alle Nass- und Trockenbereiche verwendbar (Dusche, Küche, Bad und Wohnraumgestaltung)&lt;/b&gt;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;', 1, 2);
INSERT INTO `oc_material` VALUES (1, 2, 'Alu Premium glänzend 3 mm', '&lt;p&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;&lt;b&gt;Alu Premium glänzend 3mm - Der Schein trügt nicht!&lt;/b&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;b&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;&lt;br&gt;&lt;/span&gt;- Hochwertiger digitaler Druck mit einer glänzenden Oberfläche&lt;br&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;- yourplate Klarglanz-Schicht für noch mehr Farbtiefe&lt;br&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;- Höhere Kratzfestigkeit&lt;br&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;- Alu-Verbund in 3mm Stärke&lt;br&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;- Hitzebeständig bis 100°C&lt;br&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;- Leicht zu bearbeiten!&amp;nbsp; (Handelsübliche Bohrer, Kronenbohrer oder Stichsäge)&lt;br&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;- Für alle Nass- und Trockenbereiche verwendbar (Dusche, Küche, Bad und Wohnraumgestaltung)&lt;/span&gt;&lt;/b&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;', 1, 3);
INSERT INTO `oc_modification` VALUES (1, 'd_admin_menu', 'd_admin_menu', 'Dreamvention', '1.0.0', 'http://www.dreamvention.ee/', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n    <name>d_admin_menu</name>\n    <code>d_admin_menu</code>\n    <description>Admin Menu</description>\n    <version>1.0.0</version>\n    <author>Dreamvention</author>\n    <link>http://www.dreamvention.ee/</link>\n\n        <!-- 201-220 -->\n        <file path=\"admin/view/template/common/column_left.tpl\">\n            <operation error=\"skip\">\n                <search><![CDATA[<nav id=\"column-left\"><?php echo $profile; ?><?php echo $menu; ?><?php echo $stats; ?></nav>]]></search>\n                <add position=\"replace\"><![CDATA[\n                    <nav id=\"column-left\"><?php echo $profile; ?><?php echo $menu; ?><?php echo (isset($d_admin_menu)) ? $d_admin_menu : \'\'; ?><?php echo $stats; ?></nav>\n                ]]></add>\n            </operation>\n        </file>\n\n        <!-- 230 -->\n        <file path=\"admin/view/template/common/column_left.tpl\">\n            <operation error=\"skip\">\n                <search><![CDATA[<div id=\"stats\">]]></search>\n                <add position=\"before\"><![CDATA[\n                    <?php echo (isset($d_admin_menu)) ? $d_admin_menu : \'\'; ?>\n                ]]></add>\n            </operation>\n        </file>\n\n        <!-- OC 3.x compatibility START -->\n        <file path=\"admin/view/template/common/column_left.twig\">\n            <operation error=\"skip\">\n                <search><![CDATA[<div id=\"stats\">]]></search>\n                <add position=\"before\"><![CDATA[\n                    {{ d_admin_menu }}\n                ]]></add>\n            </operation>\n        </file>\n        <!-- OC 3.x compatibility END -->\n\n</modification>\n', 1, '2019-10-03 10:36:55');
INSERT INTO `oc_modification` VALUES (2, 'd_twig_manager', 'd_twig_manager', 'Dreamvention', '1.0.0', 'http://dreamvention.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n    <name>d_twig_manager</name>\n    <code>d_twig_manager</code>\n    <description>Adds support for twig templating engine</description>\n    <version>1.0.0</version>\n    <author>Dreamvention</author>\n    <link>http://dreamvention.com</link>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"add event support for 2.1.0.0 and below\">\n            <search><![CDATA[$file = DIR_TEMPLATE . $template;]]></search>\n            <add position=\"before\"><![CDATA[\n            //d_twig_manager.xml 1\n            $output = $this->controller(\'event/d_twig_manager/support\', array(\'route\' => $template, \'data\' => $data));\n            if($output || !file_exists( DIR_TEMPLATE . $template)){\n                return $output;\n            }\n            ]]></add>\n        </operation>\n        <operation error=\"skip\" info=\"add event support for 2.2.0.0 and above\">\n            <search><![CDATA[$output = $template->render($route . \'.tpl\']]></search>\n            <add position=\"before\"><![CDATA[\n            //d_twig.xml 2\n\n            $output = $this->controller(\'event/d_twig_manager/support\', array(\'route\' => $route, \'data\' => $data));\n            if(!$output && file_exists( DIR_TEMPLATE . $route . \'.tpl\'))\n            ]]></add>\n        </operation>\n    </file>\n</modification>', 1, '2019-10-03 10:38:02');
INSERT INTO `oc_modification` VALUES (3, 'd_event_manager', 'd_event_manager', 'Dreamvention', '3.0.0', 'http://dreamvention.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n    <name>d_event_manager</name>\n    <code>d_event_manager</code>\n    <description>Add support for events to versions before 2.3.0.0 </description>\n    <version>3.0.0</version>\n    <author>Dreamvention</author>\n    <link>http://dreamvention.com</link>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"add event support 2.1.0.0 and below\">\n            <search><![CDATA[public function __construct($registry) {]]></search>\n            <add position=\"after\"><![CDATA[\n    //d_event_manager.xml loader\n    $this->load = new d_event_manager\\Loader($this, $registry);\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"add event support 2.1.0.0 and below\">\n            <search><![CDATA[public function controller($route, $args = array()) {]]></search>\n            <add position=\"replace\"><![CDATA[\n    //d_event_manager.xml controller\n    public function controller($route, $args = array()) {\n        return $this->load->controller($route, $args);\n    }\n    \n    //this is the original controller method which is called by the d_event_menager\\Loader -> contorller method\n    public function _controller($route, $args = array()) {\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"add event support 2.1.0.0 - controller has $data instead or $args\">\n            <search><![CDATA[public function controller($route, $data = array()) {]]></search>\n            <add position=\"replace\"><![CDATA[\n    //d_event_manager.xml controller\n    public function controller($route, $args = array()) {\n        return $this->load->controller($route, $args);\n    }\n    \n    //this is the original controller method which is called by the d_event_menager\\Loader -> contorller method\n    public function _controller($route, $data = array()) {\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"add event support 2.1.0.0 and below\">\n            <search><![CDATA[public function model($model) {]]></search>\n            <add position=\"replace\"><![CDATA[\n    //d_event_manager.xml model\n    public function model($model) {\n        return $this->load->model($model);\n    }\n    \n    //this is the original controller method which is called by the d_event_menager\\Loader -> contorller method\n    public function _model($model) {\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"add event support 2.1.0.0 - model has an extra $data arg\">\n            <search><![CDATA[public function model($model, $data = array()) {]]></search>\n            <add position=\"replace\"><![CDATA[\n    //d_event_manager.xml model\n    public function model($model) {\n        return $this->load->model($model);\n    }\n    \n    //this is the original controller method which is called by the d_event_menager\\Loader -> contorller method\n    public function _model($model) {\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"add event support 2.1.0.0 and below\">\n            <search><![CDATA[$this->registry->set(\'model_\' . str_replace(\'/\', \'_\', $model), new $class($this->registry));]]></search>\n            <add position=\"replace\"><![CDATA[\n        //d_event_manager.xml proxy\n        $d_event_manager = $this->registry->get(\'config\')->get(\'d_event_manager_setting\');\n        if(!$d_event_manager){\n            $this->registry->get(\'config\')->load(\'d_event_manager\');\n            $d_event_manager = $this->registry->get(\'config\')->get(\'d_event_manager_setting\');\n        }\n\n        $found = false;\n        foreach ($d_event_manager[\'skipped_models\'] as $skipped_model) {\n            if (strpos($class, $skipped_model) !== false) {\n                $found = true;\n            }\n        }\n        \n        if($found == false){\n            $proxy = new d_event_manager\\Proxy();\n            foreach (get_class_methods($class) as $method) {\n                $proxy->{$method} = $this->load->callback($this->registry, $model . \'/\' . $method);\n            }\n\n            $this->registry->set(\'model_\' . str_replace(array(\'/\', \'-\', \'.\'), array(\'_\', \'\', \'\'), (string)$model), $proxy);\n        }else{\n            $this->registry->set(\'model_\' . str_replace(\'/\', \'_\', $model), new $class($this->registry));\n        }\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"add event support 2.1.0.0 and below\">\n            <search><![CDATA[public function view($template, $data = array()) {]]></search>\n            <add position=\"replace\"><![CDATA[\n    //d_event_manager.xml view\n    public function view($route, $args = array()) {\n        return $this->load->view($route, $args);\n    }\n    \n    //this is the original controller method which is called by the d_event_menager\\Loader -> contorller method\n    public function _view($template, $data = array()) {\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"system/engine/loader.php\">\n        <operation error=\"skip\" info=\"add event support 2.1.0.0 and below\">\n            <search><![CDATA[public function config($config) {]]></search>\n            <add position=\"replace\"><![CDATA[\n    //d_event_manager.xml controller\n    public function config($route) {\n        return $this->load->config($route);\n    }\n    \n    //this is the orig